CREATE TABLE `sp_metric_expression`
(
    `id`         int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
    `attributes` varchar(1024) NOT NULL DEFAULT '' COMMENT '扩展信息',
    `expression` varchar(4096) NOT NULL DEFAULT '' COMMENT '聚合表达式',
